export default {
	onSubmit: () => {
		api_login_user.run()
			.then(() => {
				if(api_login_user.data.success) {
					storeValue("token", api_login_user.data.token);
					showAlert("Login success");
				} else showAlert("Failed to login, possibly an invalid PIN.");
			})
			.catch(() => showAlert("Failed to login, possibly an invalid PIN."));
	},
	onTextChanged: () => {
		if(LoginForm.formData.pin.length == 6) {
			pin_autologin.onSubmit();
		}
	}
}
