export default {
	onSubmit: () => {
		api_login_user.run()
			.then(() => {
				if(api_login_user.data.success) {
					storeValue("token", undefined);
					storeValue("user", undefined);
					storeValue("token", api_login_user.data.token);
					storeValue("user", api_login_user.data.employee);
					showAlert("Login success", "success");
					navigateTo("lot_dashboard");
				} else showAlert("Failed to login, possibly an invalid PIN.", "error");
			})
			.catch(() => showAlert("Failed to login, possibly an invalid PIN.", "error"));
	},
	onTextChanged: () => {
		if(LoginForm.formData.pin.length == 4) {
			pin_autologin.onSubmit();
		}
	}
}
