export default {
	findDepartment: (row) => {
		let departmentId = 0;
		if(typeof row === 'number')
			departmentId = row;
		else
			departmentId = row.sort((a, b) => b.count - a.count)[0]?.id;

		var index = GetAllDepartments.data.map(d => d.id).indexOf(departmentId);
		return GetAllDepartments.data[index]?.name;
	},
	getAssignments: (assignments) => {
		let data = [];

		console.log(typeof assignments);
		assignments.forEach(e => data.push({ 
			id: e.id, 
			name: FindDepartmentHelper.findDepartment(e.id), 
			count: e.count }
		));

		return data;
	}
}
