export default {
	reassign: () => {
		if(AssignmentsTable.updatedRows.length == 0)
			return;

		let data = Array.from(GetIndividualLot.data.assignments);	
		for(let e of AssignmentsTable.updatedRows) {
			data[ReassignmentsHelper.findIndex(e.id)].count = e.updatedFields.count;
		}

		storeValue('reassignment', data);
		UpdateLotAssignments.run().then(() => GetIndividualLot.run());
	},
	findIndex: (id) => {
		return GetIndividualLot.data.assignments.map(e => e.id).indexOf(id);
	},
	reassignmentDisplay: (reassignment) => {
		let data = "";
		for(let i = 0; i < reassignment.length; i++) {
			let e = reassignment[i];
			data += FindDepartmentHelper.findDepartment(e.id) + ": " + e.count;

			if((i + 1) < reassignment.length)
				data += ", ";
		}

		return data;
	},
	auditDisplay: (row) => {
		if(row.type === "LOT_REASSIGNED") {
			let reassignments = JSON.parse(row.data);
			return `Old:\n${ReassignmentsHelper.reassignmentDisplay(reassignments.old)}\nNew:\n${ReassignmentsHelper.reassignmentDisplay(reassignments.updated)}`;
		} else if(row.type === "NOTE_DELETED") {
			let note = JSON.parse(row.data);
			if(ShowDeletedNotesSwitch.isSwitchedOn) {
				return `${note.data}\nTimestamp: ${note.timestamp} UTC\nCreated By: ${note.createdBy}`;
			} else
				return `Note from ${note.createdBy} was deleted.`;
		} else 
			return row.data;
	}
}
