actionConfiguration.body:
row => {
  if (row.type === "LOT_REASSIGNED") {
    let reassignments = JSON.parse(row.data);
    return `Old:\n${ReassignmentsHelper.reassignmentDisplay(reassignments.old)}\nNew:\n${ReassignmentsHelper.reassignmentDisplay(reassignments.updated)}`;
  } else return row.data;
}

